mapper.select:
select *
from tz_user_score_detail
where user_id = #{userId}
and expire_time < #{expireTime}
and status = #{status}

select *
from tz_user_score_detail
where user_id = #{userId}
and status = #{status}
order by create_time

select *
from tz_user_score_detail
where user_id = #{userId}
and status = #{status}
order by create_time
limit #{current} , #{size}

